CREATE FUNCTION "CBSDB"."F_CONVERT_SERIAL"(
		CARDNO VARCHAR(20),--卡号
		offset INT,--偏移量
		len    INT--切取长度
		)
		RETURNS VARCHAR(8)
		--取第offset到(offset+len)位转数值，再转VARCHAR(8),不足前部补0
		BEGIN
			DECLARE TEMP VARCHAR(10);--
			DECLARE RESU VARCHAR(8);--
			SET TEMP=SUBSTR(CARDNO,offset,len);--
			IF (SUBSTR(TEMP,1,1)!='0')
			 THEN SET RESU=LEFT(TEMP,8);--
			ELSE
			 SET RESU=RIGHT(DIGITS(INT(TEMP)),8);--
			END IF;--
			RETURN RESU;--
		END;

