CREATE FUNCTION "CBSDB"."F_GETSEQUENCE"(
		LGRPCD       VARCHAR(4),--法人代码
		ORGANIZATION VARCHAR(5),--组织代号
		CURRENCY     VARCHAR(2),--币种
		PRODUCT      VARCHAR(4)--产品号
		)
		RETURNS BIGINT
		--获取序号值
		BEGIN
			DECLARE SEQUENCENAME VARCHAR(30);--
			DECLARE SQL_STMT VARCHAR(200);--
			DECLARE RESU BIGINT;--
			SET SEQUENCENAME ='XH_'||LGRPCD||'_FDPACCT'||ORGANIZATION||CURRENCY||PRODUCT;--
			SET SQL_STMT='SELECT NEXTVAL FOR '||SEQUENCENAME||' FROM SYSIBM.SYSDUMMY1 INTO RESU';--
			EXECUTE IMMEDIATE SQL_STMT;--
			RETURN RESU;--
		END;

