CREATE FUNCTION "CBSDB"."F_INTEREST"(
		MTDATE VARCHAR(8)--起息日
		)
		RETURNS VARCHAR(8)
		BEGIN
			DECLARE TEMP VARCHAR(4);--
			DECLARE SUFFIX VARCHAR(4);--
			SET TEMP=SUBSTR(MTDATE,5,4);--
			SET SUFFIX=
				CASE
					WHEN TEMP<'0321' THEN '0321'
					WHEN TEMP<'0621' THEN '0621'
					WHEN TEMP<'0921' THEN '0921'
					ELSE '1221'
				END;--
			RETURN CONCAT(SUBSTR(MTDATE,1,4),SUFFIX);--
		END;

