CREATE PROCEDURE "CBSDB"."PR_TRANSPLANT"(

		IN  TRANSDATE VARCHAR(8),--移植日期

		IN  TRANSTIME BIGINT     --移植时间

		)

		BEGIN

			DECLARE CUSTAC     VARCHAR(20) ;--电子账号

			DECLARE IDTYPE     VARCHAR(2)  ;--证件类型

			DECLARE IDINFO     VARCHAR(20) ;--证件号

			DECLARE CUSTNO     VARCHAR(20) ;--客户号

			DECLARE ACCTNM     VARCHAR(20) ;--客户姓名

			DECLARE TRANPW     VARCHAR(200);--支付密码

			DECLARE YLSJHM     VARCHAR(11) ;--手机号

			DECLARE BZBRCH     VARCHAR(5)  ;--组织代号

			DECLARE TRAMOT     DECIMAL     ;--账户余额

			DECLARE OPENDATE   VARCHAR(8)  ;--开户日期

			--DECLARE TRANSDATE  VARCHAR(8)  ;--移植日期

			--DECLARE TRANSTIME  BIGINT      ;--移植时间

			DECLARE ACCOUNT    VARCHAR(20) ;--系统账号

			DECLARE SERNUB     BIGINT      ;--序号
			
			--VALUES TO_CHAR(CURRENT DATE,'YYYYMMDD') INTO TRANSDATE;  --初始化日期
			--VALUES BIGINT(REPLACE(CHAR(CURRENT TIME),'.','')) INTO TRANSTIME;--初始化时间

			--SET TRANSTIME=BIGINT(REPLACE(CHAR(CURRENT TIME),'.',''));--
			SET SERNUB   =  1;                                       --初始化序号

			FOR V AS MYCURSOR CURSOR FOR SELECT * FROM DJXNXYZ WHERE RECDST='0'

			DO

				BEGIN

					SET CUSTAC   =V.CUSTAC;--

					SET IDTYPE   =V.IDTYPE;--
					SET IDINFO   =V.IDINFO;--
					SET CUSTNO   =V.CUSTNO;--
					SET ACCTNM   =V.ACCTNM;--
					SET TRANPW   =V.TRANPW;--
					SET YLSJHM   =V.YLSJHM;--
					SET BZBRCH   =V.BZBRCH;--
					SET TRAMOT   =V.TRAMOT;--
					SET OPENDATE =V.OPDATE;--
					SET ACCOUNT  =F_GENERATE_ACCOUNT(V.BZBRCH,SERNUB);--
					CALL PR_OPEN_ACCOUNT(CUSTAC,IDTYPE,IDINFO,CUSTNO,ACCTNM,TRANPW,YLSJHM,BZBRCH,TRAMOT,OPENDATE,TRANSDATE,TRANSTIME,ACCOUNT,SERNUB);--
					SET SERNUB   =SERNUB + 1;--

				END;--

			END FOR;--

		END;

